var betInits=[];function numToDollarAmount(t){const e=t.toFixed(2);return new Intl.NumberFormat("en-US",{style:"currency",currency:"USD"}).format(e)}function getWinnings(t,e){var n=0;if(t<0){var i=100*e;n=.001*Math.abs(i/t)}else{n=.001*((i=e*t)/100)}return n}class BetInit{constructor(t,e,n,i,s,o,a){this.gameId=t,this.type=e,this.amount=n,this.odds=i,this.total=s,this.toWin=getWinnings(this.odds,this.amount).toFixed(2)}}function createBetInit(t,e,n,i,s){const o=new BetInit(t,e,n,i,s,away,home);betInits.push(o);var a=document.getElementById("betinits"),l=a.innerHTML+('<div class="d-flex align-items-center justify-content-between py-8"><div class="d-flex flex-column mr-2 align-middle"> <a href="#" class="text-dark-75 slip-bet-text text-hover-primary">'+name+" "+i+'</a> <span class="text-muted">Risk '+numToDollarAmount(n)+" to win "+toWin+' Points</span></div> <a href="#" class="btn btn-sm btn-icon btn-circle btn-light-danger"> <i class="ki ki-close icon-xs text-muted"></i> </a></div><div class="separator separator-solid"></div>');a.innerHTML=l}function clicker(){document.getElementById("sliderInput").value;console.log(selectedGame),console.log(selectedType)}